<!DOCTYPE html><html lang="fr" data-fr-scheme="light" prefix="og: http://ogp.me/ns#"><head><meta charSet="utf-8"/><meta name="viewport" content="width=device-width"/><link rel="preload" href="/_next/static/media/Marianne-Regular.119b3a3e.woff2" as="font" crossorigin="anonymous"/><link rel="preload" href="/_next/static/media/Marianne-Medium.452138fa.woff2" as="font" crossorigin="anonymous"/><link rel="preload" href="/_next/static/media/Marianne-Bold.f0ef9bad.woff2" as="font" crossorigin="anonymous"/><link rel="apple-touch-icon" href="/_next/static/media/apple-touch-icon.1db30588.png"/><link rel="icon" href="/_next/static/media/favicon.f453a8cf.svg" type="image/svg+xml"/><link rel="shortcut icon" href="/_next/static/media/favicon.8c087985.ico" type="image/x-icon"/><style id="dsfr-root-color-scheme">:root { color-scheme: light; }</style><meta name="theme-color" content="#ffffff"/><script>
{

    window.ssrWasPerformedWithIsDark = "light" === "dark";
	const sanitizer = typeof trustedTypes !== "undefined" ? trustedTypes.createPolicy("react-dsfr-asap", { createHTML: s => s }) : {
		createHTML: s => s,
	};

    const isDark = (() => {

    	const isDarkExplicitlyProvidedAsParameter = (() => {
    		if ("light" === "system") {
    			return undefined;
    		}

    		switch ("light") {
    			case "dark": return true;
    			case "light": return false;
    		}
    	})();

    	const isDarkFromLocalStorage = (() => {
    		const colorSchemeReadFromLocalStorage = localStorage.getItem("scheme");

    		if (colorSchemeReadFromLocalStorage === null) {
    			return undefined;
    		}

    		if (colorSchemeReadFromLocalStorage === "system") {
    			return undefined;
    		}

    		switch (colorSchemeReadFromLocalStorage) {
    			case "dark":
    				return true;
    			case "light":
    				return false;
    		}
    	})();

    	const isDarkFromOsPreference = (() => {
    		if (!window.matchMedia) {
    			return undefined;
    		}

    		return window.matchMedia("(prefers-color-scheme: dark)").matches;
    	})();

    	const isDarkFallback = false;

    	return (
    		isDarkFromLocalStorage ??
    		isDarkExplicitlyProvidedAsParameter ??
    		isDarkFromOsPreference ??
    		isDarkFallback
    	);

    })();

    ["data-fr-scheme", "data-fr-theme"].forEach(attr => document.documentElement.setAttribute(attr, isDark ? "dark" : "light"));

    {

        let element= document.getElementById("dsfr-root-color-scheme");

		if( element !== null ){
			element.remove()
		}

        element = document.createElement("style");

		if ("" !== "") {
			element.setAttribute("nonce", "");
		}

        element.id = "dsfr-root-color-scheme";

        element.innerHTML = sanitizer.createHTML(`:root { color-scheme: ${isDark ? "dark" : "light"}; }`);

        document.head.appendChild(element);

    }

    {

        const name = "theme-color";

        let element = document.querySelector(`meta[name=${name}]`);

		if( element !== null ){
			element.remove();
		}

        element = document.createElement("meta");

        element.name = name;

        element.content = isDark ? "#161616" : "#ffffff";

        document.head.appendChild(element);

    }

