    const iconLoader = document.getElementById('icon-loader')

    const levels = {
        COMM: 'Commune',
        EPCI: 'EPCI',
        SCOT: 'SCOT',
        DEPART: 'Département',
        REGION: 'Région',
    }

    const debounce = (func, timeout = 300) => {
        let timer
        return (...args) => {
            clearTimeout(timer)
            timer = setTimeout(() => { func.apply(this, args) }, timeout)
        }
    }


    const searchTerritory = (value) => {
        if (value.length <= 1) {
            results.innerHTML = ''
            return
        }

        toggleLoader(true)

        fetch('/public/search-land', {
            method: 'POST',
            body: JSON.stringify({
                needle: value,
            }),
            headers: {
                'X-CSRFToken': 'MZRycB6AhZ3nthEPMXzzZWh9steOzqoYR9nSdNXb3dPnVmmx7xvnsnZhgfixZHV6',
                'Content-type': 'application/json; charset=UTF-8',
            },
        })
        .then((response) => {
            return response.json()
        })
        .then((response) => {
            let count = 0
            results.innerHTML = Object.keys(response)
            .map((key) => {
                if (response[key].length > 0) {
                    count++

                    return `
                        <div class='level'>${levels[key]}</div>
                        ${response[key].map(obj => (
                            `<div class="territory" id="territory-${obj.public_key}">
                                <div class='fr-mr-2v'>${obj.name}</div>
                                ${obj.source_id !== "" ? `<div class='text-muted'>(${obj.source_id})</div>` : ``}
                            </div>`
                        )).join('')}
                    `
                }
            }).join('')

            if (count === 0)
                results.innerHTML = '<div class="no-results fr-text--sm text-muted">Il n\'y a aucun résultat pour votre recherche</div>'
        })
        .catch((error) => console.log('Erreur : ' + error))
        .finally(() => {
            toggleLoader(false)
        })
    }

    const toggleLoader = (value) => {
        iconSearch.hidden = value
        iconLoader.hidden = !value  
    }

    const createDiagnostic = (public_key) => {
        let form = document.createElement('form')
        form.action = '/project/nouveau'
        form.method = 'POST'

        let csrfToken = document.createElement('input')
        csrfToken.type = 'hidden'
        csrfToken.name = 'csrfmiddlewaretoken'
        csrfToken.value = 'MZRycB6AhZ3nthEPMXzzZWh9steOzqoYR9nSdNXb3dPnVmmx7xvnsnZhgfixZHV6'
        form.appendChild(csrfToken)

        let selection = document.createElement('input')
        selection.type = 'hidden'
        selection.name = 'selection'
        selection.value = public_key
        form.appendChild(selection)

        let a = document.createElement('input')
        a.type = 'hidden'
        a.name = 'keyword'
        a.value = searchInput.value
        form.appendChild(a)

        document.body.appendChild(form)
        form.submit()
    }

    // Hide results on click outside search bar
    document.addEventListener('click', (e) => {   
        if (!searchBox.contains(e.target)){ 
            results.hidden = true
            results.innerHTML = ''
            searchInput.value = ''
        }
        else {
            results.hidden = false
        }
        if (!!e.target.id && e.target.id.startsWith('territory-')) {
            const [, public_key] = e.target.id.split('territory-')
            createDiagnostic(public_key)
        }
    })

    const onInput = debounce(event => searchTerritory(event.target.value), 250)
    searchInput.addEventListener('input', onInput)
