        REGION: 'Région',
    }

    const debounce = (func, timeout = 300) => {
        let timer
        return (...args) => {
            clearTimeout(timer)
            timer = setTimeout(() => { func.apply(this, args) }, timeout)
        }
    }


    const searchTerritory = (value) => {
        if (value.length <= 1) {
            results.innerHTML = ''
            return
        }

        toggleLoader(true)

        fetch('/public/search-land', {
            method: 'POST',
            body: JSON.stringify({
