        selection.value = public_key
        form.appendChild(selection)

        let a = document.createElement('input')
        a.type = 'hidden'
        a.name = 'keyword'
        a.value = searchInput.value
        form.appendChild(a)

        document.body.appendChild(form)
        form.submit()
